// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract ImagineKitPlatform {
    struct Experience {
        address creator;
        uint256 price;                 // Price in Fan Tokens
        uint256 usageCount;            // Number of times the experience is used
        bool isActive;                 // Is the experience active?
        uint256 platformFeePercentage; // Platform fee percentage
        uint256 rewardPool;            // Pool to reward users
    }

    IERC20 public fanToken;
    address public platformOwner;
    mapping(uint256 => Experience) public experiences;
    mapping(address => uint256) public earnings;
    uint256 public experienceCounter;

    event ExperienceRegistered(uint256 indexed experienceId, address indexed creator, uint256 price, uint256 platformFeePercentage);
    event ExperienceUsed(uint256 indexed experienceId, address indexed user, uint256 fee);
    event EarningsWithdrawn(address indexed creator, uint256 amount);
    event RewardsClaimed(address indexed user, uint256 amount);

    modifier onlyCreator(uint256 _experienceId) {
        require(experiences[_experienceId].creator == msg.sender, "Only the creator can manage this experience");
        _;
    }

    constructor(IERC20 _fanToken) {
        fanToken = _fanToken;
        platformOwner = msg.sender;
    }

    function registerExperience(uint256 _price, uint256 _platformFeePercentage) external {
        require(_price > 0, "Price must be greater than zero");
        require(_platformFeePercentage <= 100, "Platform fee must be between 0 and 100");

        experienceCounter++;
        experiences[experienceCounter] = Experience({
            creator: msg.sender,
            price: _price,
            usageCount: 0,
            isActive: true,
            platformFeePercentage: _platformFeePercentage,
            rewardPool: 0
        });

        emit ExperienceRegistered(experienceCounter, msg.sender, _price, _platformFeePercentage);
    }

    function useExperience(uint256 _experienceId) external {
        Experience storage experience = experiences[_experienceId];
        require(experience.isActive, "This experience is not active");
        require(fanToken.balanceOf(msg.sender) >= experience.price, "Insufficient token balance");

        uint256 platformFee = (experience.price * experience.platformFeePercentage) / 100;
        uint256 creatorEarnings = experience.price - platformFee;

        // Transfer Fan Tokens to platform and creator
        fanToken.transferFrom(msg.sender, platformOwner, platformFee);
        fanToken.transferFrom(msg.sender, experience.creator, creatorEarnings);

        // Update usage count and reward pool
        experience.usageCount++;
        experience.rewardPool += (platformFee * 10) / 100; // 10% of the platform fee goes to reward pool

        emit ExperienceUsed(_experienceId, msg.sender, experience.price);
    }

    function withdrawEarnings() external {
        uint256 amount = earnings[msg.sender];
        require(amount > 0, "No earnings to withdraw");

        earnings[msg.sender] = 0;
        fanToken.transfer(msg.sender, amount);

        emit EarningsWithdrawn(msg.sender, amount);
    }

    function setExperiencePrice(uint256 _experienceId, uint256 _price) external onlyCreator(_experienceId) {
        require(_price > 0, "Price must be greater than zero");
        experiences[_experienceId].price = _price;
    }

    function toggleExperienceStatus(uint256 _experienceId, bool _isActive) external onlyCreator(_experienceId) {
        experiences[_experienceId].isActive = _isActive;
    }

    function claimRewards(uint256 _experienceId) external {
        Experience storage experience = experiences[_experienceId];
        require(experience.rewardPool > 0, "No rewards available");

        uint256 userReward = experience.rewardPool;
        experience.rewardPool = 0;

        fanToken.transfer(msg.sender, userReward);

        emit RewardsClaimed(msg.sender, userReward);
    }

    function getExperienceDetails(uint256 _experienceId)
        external
        view
        returns (
            address creator,
            uint256 price,
            uint256 usageCount,
            bool isActive,
            uint256 platformFeePercentage,
            uint256 rewardPool
        )
    {
        Experience memory experience = experiences[_experienceId];
        return (experience.creator, experience.price, experience.usageCount, experience.isActive, experience.platformFeePercentage, experience.rewardPool);
    }
}

